// https://github.com/isaacs/sax-js/issues/49
require(__dirname).test
  ( { xml : "<xml><script>Satans Demokrati</script></xml>"
    , expect :
      [ [ "opentag", { name: "xml", attributes: {} } ]
      , [ "opentag", { name: "script", attributes: {} } ]
      , [ "text", "Satans Demokrati" ]
      , [ "closetag", "script" ]
      , [ "closetag", "xml" ]
      ]
    , strict : false
    , opt : { lowercasetags: true, noscript: true }
    }
  )

require(__dirname).test
  ( { xml : "<xml><script><![CDATA[Satans Demokrati]]></script></xml>"
    , expect :
      [ [ "opentag", { name: "xml", attributes: {} } ]
      , [ "opentag", { name: "script", attributes: {} } ]
      , [ "opencdata", undefined ]
      , [ "cdata", "Satans Demokrati" ]
      , [ "closecdata", undefined ]
      , [ "closetag", "script" ]
      , [ "closetag", "xml" ]
      ]
    , strict : false
    , opt : { lowercasetags: true, noscript: true }
    }
  )

